import React from 'react';
import './App.css'; // Assuming that your main styles are in App.css

const TeamCard = ({ teamName, teamRecord, teamScore, teamLogo }) => (
  <div className="team-card">
    <div className="avatar-container">
      <img src={teamLogo} alt={`${teamName} Logo`} className="team-logo" />
    </div>
    <div className="team-info">
      <div className="team-name">{teamName}</div>
      <div className="team-record">{teamRecord}</div>
    </div>
    <div className="team-score">{teamScore}</div>
  </div>
);

const ScoreboardLayout = ({ team1, team2 }) => {
  return (
    <div className="league-panel">
      <div className="matchup-panel">
        <div className="matchup-header-container">
          <TeamCard {...team1} />
          <div className="vs-label">VS</div>
          <TeamCard {...team2} />
        </div>
      </div>
    </div>
  );
};

export default ScoreboardLayout;
